      categorie: ['PNL Constanţa', 'Politică'],
      etichete: ['Partidul National Liberal', 'PRIN NOI ÎNȘINE'],
      slug: 'prin-noi-insine-dar-cand-de-ce-continuam-sa-esuam-ca-tara',
