      slug: 'pe-strazile-oraselor-din-dobrogea-oamenii-erau-siliti-a-canta-in-batjocura-la-arme-sau-destepta-te-romane-ca-sa-poata-fi-apoi-huiduiti-si-batuti-tot-in-batjocura',
